import { createStore } from 'vuex';

export default createStore({
  state: {
    user: JSON.parse(localStorage.getItem('user')) || null,
    token: localStorage.getItem('token') || null,
    products:[],
    isLoggedIn: localStorage.getItem("authToken") ? true : false, 
    cart: []
  },
  mutations: {
    setLoggedIn(state, status){
      state.isLoggedIn = status
    },
    setUser(state, user) {
      state.user = user;
      localStorage.setItem('user', JSON.stringify(user));
    },
    setToken(state, token) {
      state.token = token;
      localStorage.setItem('token', token);
    },
    logout(state) {
      state.isLoggedIn = false;
      state.user = null;
      state.token = null;
      localStorage.removeItem('user');
      localStorage.removeItem('authToken');
    },
    setProducts(state, products) {
      state.products = products;
  },
  setCart(state, cartItems){
    state.cart = cartItems;
  },
  addCartItem(state, item){
    const exists = state.cart.find(cartItem => cartItem.product_id === item.product_id);
    if (exists){
      exists.quantity += item.quantity;
    } else {
      state.cart.push(item);
    }
  },
  removeCartItem(state, product_id){
    state.cart = state.cart.filter(item => item.product_id !== product_id);
  },
  updateQuantity(state, { product_id, quantity }) {
    const item = state.cart.find(cartItem => cartItem.product_id === product_id);
    if (item) {
      item.quantity = quantity;
    }
  },
  clearCart(state){
    state.cart = []
  }
  },
  actions: {

async fetchProducts({ commit }) {
  try {
    const response = await fetch('http://localhost:3000/products');
    const data = await response.json();
    commit('setProducts', data);
  } catch (error) {
    console.error('Error fetching products:', error);
  }
},
async register({ commit }, { userData, router }) {
  try {
    const response = await fetch('http://localhost:3000/users/register', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(userData)
    });
    const data = await response.json();

    if (response.ok) {
      commit('setUser', data.user);
      commit('setToken', data.token);
      alert('Registration successful');
      router.push('/login');
    } else {
      alert(data.message);
    }
  } catch (error) {
    console.error(error);
    alert('Registration failed');
  }
},

async login({ commit }, { loginData, router }) {
  try {
    const response = await fetch('http://localhost:3000/users/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(loginData)
    });
    const data = await response.json();

    if (!response.ok) {
      alert(data.message || 'Login failed');
      return;
    }
    if(response.ok){
      localStorage.setItem("authToken", data.token);
      localStorage.setItem("user", JSON.stringify(data.user));
      commit("setLoggedIn", true);
      commit("setUser", data.user);
      alert('Login successful');
      router.push('/');
    }
    // commit('setToken', data.token);
    // commit('setUser', data.user);
  
  } catch (error) {
    console.error(error);
    alert('Login failed');
  }
},
logout({commit}){
  commit ("Logout")
},
async getCart({ commit }, user_id) {
  if (!user_id) return;
  try {
    const response = await fetch(`http://localhost:3000/cart/${user_id}`);
    const data = await response.json();
    commit('setCart', data);
  } catch (error) {
    console.error("Error fetching cart", error);
  }
},

async addCartItem({ commit, rootState }, { product_id, quantity }) {
  const user_id = rootState.user?.user_id;
  if (!user_id) return alert("Please log in first!");
  try {
    const response = await fetch('http://localhost:3000/cart/add', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ user_id, product_id, quantity })
    });
    if (!response.ok) throw new Error("Failed to add item to cart");
    commit("addCartItem", { product_id, quantity });
  } catch (error) {
    console.error("Error adding the item to cart", error);
  }
},

async removeCartItem({ commit, rootState }, product_id) {
  const user_id = rootState.user?.user_id;
  if (!user_id) return alert("Please log in first!");
  try {
    const response = await fetch("http://localhost:3000/cart/remove", {
      method: "DELETE",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ user_id, product_id })
    });
    if (!response.ok) throw new Error("Failed to remove item from cart");
    commit("removeCartItem", product_id);
  } catch (error) {
    console.error("Error removing the item from cart:", error);
  }
},

async updateQuantity({ commit, rootState }, { product_id, quantity }) {
  const user_id = rootState.user?.user_id;
  if (!user_id) return alert("Please log in first!");
  try {
    const response = await fetch("http://localhost:3000/cart/update", {
      method: "PATCH",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ user_id, product_id, quantity })
    });
    if (!response.ok) throw new Error("Failed to update item quantity");
    commit("updateQuantity", { product_id, quantity });
  } catch (error) {
    console.error("Error updating quantity:", error);
  }
}
  },
  getters:{
    allProducts: (state) => state.products,
    isLoggedIn: (state)=> state.isLoggedIn,
    user: (state) => state.user,
    cartTotal: state => state.cart.reduce((total, item) => total + item.price * item.quantity, 0),
    cartItemCount: state => state.cart.reduce((count, item) => count + item.quantity, 0)
  }
});
